UPDATE field_settings SET actions = ? WHERE field_settings_id <> 0 AND field_settings_id IN
( SELECT field_settings_id WHERE column_name = ? AND field_type = ?
AND ddp_instance_id in (SELECT ddp_instance_id WHERE instance_name = "singular"));

